package com.bublesort;/** @author Mauricio */public class Main{  void bubleSort(int[] arr, int sz, int J){      do{        boolean swapped = true;        for (J = 1; J < sz; J++) {          if (arr[J - 1] > arr[J]) {            int tmp = arr[J - 1];            arr[J - 1] = arr[J];            arr[J] = tmp;            swapped = false;          }        }      ++J;      if(swapped) {        break;      }    } while(true);  }  public static void main(String[] args) {    int[] idades = {12, 54, 2};    int len = idades.length;    Main bbsort = new Main();    int let = 0;    bbsort.bubleSort(idades, len, let);    for (int idade : idades) {      System.out.println(idade);    }  }}